import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom';

const logo = require('../images/sass_logo.png');//for reusability

export default class Nav extends React.Component{
  state = {
    color: 'white'
  }

  listenScrollEvent = e => {
    if (window.scrollY > 400) {
      this.setState({color: 'black'})
    } else {
      this.setState({color: 'white'})
    }
  }

  componentDidMount() {
    window.addEventListener('scroll', this.listenScrollEvent)
  }

  render(){
    return(
      <nav className="navbar navbar-expand-lg navbar-dark bg-transparent fixed-top" style={{color: this.state.color}}>
        <div className="container-fluid">
        <a className="navbar-brand" href="#">
          <img src={logo} className="logo" alt="logo"/>
        </a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown"
                aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNavDropdown">
        <ul className="navbar-nav ml-auto">
            <li className="nav-item"><Link exact to={'/'} className="nav-link current"> HOME </Link></li>
            <li className="nav-item"><Link to={'/contact'} className="nav-link">CONTACT</Link></li>
            <li className="nav-item"><Link to={'/about'} className="nav-link">ABOUT</Link></li>
          </ul>
        </div>
        </div>
        </nav>
    );
  }
}
